import { Component, OnInit } from '@angular/core';
import { TvMazeService } from '../../services/tv-maze.service';
import { map } from 'rxjs/operators';
import { Router } from '@angular/router';

@Component({
  selector: 'app-series-search',
  templateUrl: './series-search.component.html',
  styleUrls: ['./series-search.component.css'],
})
export class SeriesSearchComponent implements OnInit {
  searchQuery: string = 'girls';
  searchResults: any[] = [];
  constructor(private tvMazeService: TvMazeService, private router: Router) {}

  ngOnInit(): void {}

  search(query: string) {
    this.tvMazeService
      .getSeries(query)
      .pipe(map((data: any[]) => data.map((item) => item.show)))
      .subscribe(
        (processedData) => {
          console.log('Processed results:', processedData);
          this.searchResults = processedData;
        },
        (error) => {
          console.error('Error fetching search results:', error);
        }
      );
  }

  navigateToDetails(showId: number) {
    // Programmatically navigate to the details page using the router
    this.router.navigate(['/details', showId]);
  }
}
<div class="container m-3">
  <div class="row">
    <div class="col-sm">
      <h2>Search for TV Shows</h2>
      <input
        #searchInput
        placeholder="Enter search query"
        class="form-control"
      />
      <button (click)="search(searchInput.value)">Search</button>
      <div *ngIf="searchResults">
        <h3>Search Results:</h3>
        <ul>
          <li *ngFor="let result of searchResults">
            <a (click)="navigateToDetails(result.show.id)">{{
              result.show.name
            }}</a>
          </li>
        </ul>
      </div>
    </div>
  </div>
</div>
