import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TvMazeService } from '../../services/tv-maze.service';

@Component({
  selector: 'app-series-details',
  templateUrl: './series-details.component.html',
  styleUrls: ['./series-details.component.css'],
})
export class SeriesDetailsComponent implements OnInit {
  seriesId!: number;
  seriesDetails: any;
  seriesSeasons: any[] = [];
  seriesEpisodes: any[] = [];
  cast: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private tvMazeService: TvMazeService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.seriesId = +params['id'];
      this.loadSeriesDetails();
    });

    const showId = this.seriesId;

    this.tvMazeService.getSeasons(showId).subscribe(
      (seasonsData) => {
        this.seriesSeasons = seasonsData;
      },
      (error) => {
        console.error('Error fetching seasons information:', error);
      }
    );

    this.tvMazeService.getEpisodes(this.seriesId).subscribe(
      (episodesData) => {
        this.seriesEpisodes = episodesData;
      },
      (error) => {
        console.error('Error fetching episodes information:', error);
      }
    );

    this.tvMazeService.getCast(this.seriesId).subscribe(
      (data) => {
        this.cast = data;
      },
      (error) => {
        console.error('Error fetching cast details:', error);
      }
    );
  }

  loadSeriesDetails() {
    this.tvMazeService.getSeriesDetails(this.seriesId).subscribe(
      (data) => {
        console.log('Series details received', data);
        this.seriesDetails = data;
      },
      (error) => {
        console.error('Error fetching series details:', error);
      }
    );
  }
}
<div *ngIf="seriesDetails">
  <h2>{{ seriesDetails.name }}</h2>
  <img
    [src]="seriesDetails.image.medium"
    alt="{{ seriesDetails.name }} Image"
  />
  <p>{{ seriesDetails.summary }}</p>

  <div *ngIf="seriesDetails.genres && seriesDetails.genres.length > 0">
    <p><strong>Genres:</strong> {{ seriesDetails.genres.join(", ") }}</p>
  </div>

  <div *ngIf="seriesDetails.rating && seriesDetails.rating.average">
    <p><strong>Rating:</strong> {{ seriesDetails.rating.average }}/10</p>
  </div>
  <h3>Cast</h3>
  <ul>
    <li *ngFor="let actor of cast">
      <img [src]="actor.person.image.medium" alt="{{ actor.person.name }}" />
      <a [routerLink]="['/show', seriesId, 'cast', actor.person.id]">{{
        actor.person.name
      }}</a>
    </li>
  </ul>

  <div *ngIf="seriesDetails.seasons && seriesDetails.seasons.length > 0">
    <p><strong>Number of Seasons:</strong> {{ seriesSeasons.length }}</p>

    <div *ngFor="let season of seriesSeasons">
      <h4>Season {{ season.number }}</h4>

      <div class="dropdown">
        <button
          class="btn btn-secondary dropdown-toggle"
          type="button"
          id="seasonDropdown{{ season.number }}"
          data-toggle="dropdown"
          aria-haspopup="true"
          aria-expanded="false"
        >
          Episodes
        </button>
        <div
          class="dropdown-menu"
          aria-labelledby="seasonDropdown{{ season.number }}"
        >
          <a
            class="dropdown-item"
            *ngFor="let episode of seriesEpisodes"
            [routerLink]="[
              '/show',
              seriesId,
              'episodes',
              season.number,
              episode.number
            ]"
          >
            Episode {{ episode.number }} : {{ episode.name }}
          </a>
        </div>
      </div>
    </div>
  </div>
</div>
